mapper.select:
SELECT article_id, category_id, ( SELECT name FROM category WHERE category_id = article.category_id LIMIT 1 ) AS category_name, writer, password, title, content, view_count, created_date, modified_date, (SELECT EXISTS(SELECT 1 FROM article_file WHERE article_id = article.article_id)) AS is_file_exist FROM article WHERE 1=1
AND ( title like CONCAT('%',#{searchWord},'%') OR content like CONCAT('%',#{searchWord},'%') OR writer like CONCAT('%',#{searchWord},'%') )
AND category_id = #{categoryId}
AND created_date BETWEEN date(#{startDate}) AND date(#{endDate})+1
ORDER BY article_id DESC LIMIT #{pageSize} OFFSET #{rowStart}

SELECT EXISTS(
         SELECT 1
         FROM article
         WHERE article_id = #{articleId}
         LIMIT 1
         ) as success

SELECT EXISTS(
         SELECT 1
         FROM article
         WHERE article_id = #{articleId}
           AND password = #{password} LIMIT 1
         ) as success

mapper.update:
UPDATE article
SET view_count = view_count + 1
WHERE article_id = #{articleId}

mapper.delete:
DELETE
FROM article
WHERE article_id = #{articleId}
  AND password = #{password}
